import { useValidacionApi, Validacion } from '../../hooks/useValidacionApi';
import { useAuth } from '../../hooks/useAuth';
import { useState, useEffect } from 'react';
import Modal from '../../components/Modal';
import { tripsApi } from '../../services/api';
import { useReloadTrigger } from '../../pages/TripsPage';

// Utilidad para generar decenas (1, 2, 3) de los últimos 12 meses
function generarOpcionesDecenas(): string[] {
  const opciones: string[] = [];
  const ahora = new Date();
  for (let i = 0; i < 12; i++) {
    const fecha = new Date(ahora.getFullYear(), ahora.getMonth() - i, 1);
    const mes = fecha.getMonth() + 1;
    const año = fecha.getFullYear();
    opciones.push(`1${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`2${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`3${mes.toString().padStart(2, '0')}${año}`);
  }
  return opciones;
}

function getNombreDecena(decenaStr: string): string {
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  const decena = decenaStr.charAt(0);
  const fecha = new Date(año, mes - 1, 1);
  const nombreMes = fecha.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' });
  return `Decena ${decena} de ${nombreMes}`;
}

// Utilidad para obtener la decena actual y el formato de fechas igual que en otras pestañas
function getDecenaActual(): string {
  const ahora = new Date();
  const mes = ahora.getMonth() + 1;
  const año = ahora.getFullYear();
  const dia = ahora.getDate();
  let decena = 1;
  if (dia > 20) decena = 3;
  else if (dia > 10) decena = 2;
  return `${decena}${mes.toString().padStart(2, '0')}${año}`;
}

function getFechasDecena(decenaStr: string): { desde: string; hasta: string } {
  const decena = parseInt(decenaStr.charAt(0));
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  let diaInicio = 1;
  let diaFin = 10;
  if (decena === 2) {
    diaInicio = 11;
    diaFin = 20;
  } else if (decena === 3) {
    diaInicio = 21;
    const ultimoDia = new Date(año, mes, 0).getDate();
    diaFin = ultimoDia;
  }
  const desde = `${año}-${mes.toString().padStart(2, '0')}-${diaInicio.toString().padStart(2, '0')}`;
  const hasta = `${año}-${mes.toString().padStart(2, '0')}-${diaFin.toString().padStart(2, '0')}`;
  return { desde, hasta };
}

export default function ValidacionOperacional() {
  const { fetchValidaciones, validar } = useValidacionApi();
  const { user, canEditValidated } = useAuth();
  const [validaciones, setValidaciones] = useState<Validacion[]>([]);
  // Inicializar selectores igual que en otras pestañas
  const [decenaSeleccionada, setDecenaSeleccionada] = useState<string>(getDecenaActual());
  const [fechaDesde, setFechaDesde] = useState<string>('');
  const [fechaHasta, setFechaHasta] = useState<string>('');
  const [fetchError, setFetchError] = useState<string | null>(null);
  // Estado de comentarios por fila
  const [comentarios, setComentarios] = useState<{ [id: string]: string }>({});

  // Estados para el modal de cambio de estatus
  const [modalOpen, setModalOpen] = useState(false);
  const [modalIndex, setModalIndex] = useState<number | null>(null);
  const [nuevoEstatus, setNuevoEstatus] = useState<string>('');

  // Opciones de estatus
  const opcionesEstatus = [
    'PENDIENTE',
    'EN_REVISION',
    'APROBADO',
    'RECHAZADO',
  ];

  const { reloadTrigger } = useReloadTrigger();

  // Actualizar fechas cuando cambia la decena seleccionada
  useEffect(() => {
    if (decenaSeleccionada) {
      const { desde, hasta } = getFechasDecena(decenaSeleccionada);
      setFechaDesde(desde);
      setFechaHasta(hasta);
    }
  }, [decenaSeleccionada]);

  useEffect(() => {
    let isMounted = true;
    // Incluir producciones aprobadas para que se mantengan visibles
    fetchValidaciones(decenaSeleccionada, true).then(data => {
      if (isMounted) setValidaciones(data);
    }).catch(err => {
      if (isMounted) setFetchError('No tienes permisos para ver las validaciones o el servidor no está disponible.');
    });
    return () => { isMounted = false; };
    // eslint-disable-next-line
  }, [decenaSeleccionada, reloadTrigger]);

  const [trips, setTrips] = useState<any[]>([]);
  const [routes, setRoutes] = useState<any[]>([]);

  // Cargar viajes y rutas igual que en Producción
  useEffect(() => {
    tripsApi.getAllComplete().then(setTrips);
    fetch('/api/routes?page=0&size=1000')
      .then(res => res.json())
      .then(data => setRoutes(data.content || data));
  }, [decenaSeleccionada, reloadTrigger]);

  // Normalización igual que en backend
  function normalize(str: string) {
    if (!str) return '';
    return str
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '')
      .toLowerCase()
      .replace(/\s+/g, ' ')
      .trim();
  }

  // Eliminar duplicados de viajes por combinación única antes de calcular
  const viajesUnicos = Array.from(new Map(viajesParaCalcular.map(v => {
    const key = [v.travelDate, v.departureTime, normalize(v.origin), normalize(v.destination), v.busNumber].join('|');
    return [key, v];
  })).values());

  // Buscar zona por origen/destino (normalizado)
  function getZonaPorTramo(origen: string, destino: string) {
    const normOrigen = normalize(origen);
    const normDestino = normalize(destino);
    for (const route of routes) {
      const rOrig = normalize(route.origen);
      const rDest = normalize(route.destino);
      if (
        (rOrig === normOrigen && rDest === normDestino) ||
        (rOrig === normDestino && rDest === normOrigen)
      ) {
        return route.zona;
      }
    }
    return null;
  }

  // Agrupar viajes por empresa normalizada
  const normalizarEmpresa = (nombre: string) => normalize(nombre);
  const empresasNormalizadas = Array.from(new Set(viajesUnicos
    .filter(v => v.companyName && v.companyName.trim() !== "")
    .map(v => normalizarEmpresa(v.companyName!))));

  // Mapear nombre normalizado a nombre original
  const mapEmpresaOriginal = new Map<string, string>();
  viajesUnicos.forEach(v => {
    if (v.companyName) {
      const norm = normalizarEmpresa(v.companyName);
      if (!mapEmpresaOriginal.has(norm)) {
        mapEmpresaOriginal.set(norm, v.companyName);
      }
    }
  });

  // Calcular resumen por empresario (en tiempo real)
  const resumenCalculado = empresasNormalizadas.map(empNorm => {
    const viajesEmp = viajesUnicos.filter(v => normalizarEmpresa(v.companyName!) === empNorm);
    let totalServicios = 0;
    let totalIngresos = 0;
    let totalGanancia = 0;
    viajesEmp.forEach(v => {
      const branch = Number(v.branchRevenue) || 0;
      const road = Number(v.roadRevenue) || 0;
      let manual = 0;
      if (v.manualIncome !== undefined && v.manualIncome !== null && v.manualIncome !== '') {
        manual = Number(String(v.manualIncome).replace(/[^\d.\-]/g, '')) || 0;
      }
      const totalIngresosViaje = branch + road + manual;
      const zona = getZonaPorTramo(v.origin, v.destination);
      const porcentaje = zona ? zona.porcentaje : 0;
      totalServicios++;
      totalIngresos += totalIngresosViaje;
      if (zona) {
        totalGanancia += totalIngresosViaje * (porcentaje / 100);
      }
    });
    return { emp: mapEmpresaOriginal.get(empNorm) || empNorm, totalServicios, totalIngresos, totalGanancia };
  });

  // Calcular ganancia total
  const gananciaTotal = resumenCalculado.reduce((acc, r) => acc + r.totalGanancia, 0);

  const handleAbrirModal = (i: number) => {
    setModalIndex(i);
    setNuevoEstatus(resumenCalculado[i]?.emp ? (validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[i].emp))?.validado ? 'APROBADO' : 'PENDIENTE') : 'PENDIENTE');
    setModalOpen(true);
  };

  const handleGuardarModal = async () => {
    if (modalIndex === null) return;
    const r = resumenCalculado[modalIndex];
    if (!r || !r.emp) return;
    const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(r.emp) && v.decena === decenaSeleccionada);
    if (!v || !v.id) return;
    // Restricciones de rol
    if (v.validado && !canEditValidated()) {
      alert('Solo los administradores pueden cambiar el estado de producciones ya aprobadas.');
      return;
    }
    // Validar estatus permitido
    if (!canEditValidated() && v.validado && nuevoEstatus !== 'APROBADO') {
      alert('No puedes modificar una producción ya aprobada.');
      return;
    }
    if (!canEditValidated() && !['EN_REVISION', 'APROBADO', 'RECHAZADO'].includes(nuevoEstatus)) {
      alert('No tienes permiso para asignar este estatus.');
      return;
    }
    const comentarioFila = comentarios[String(v.id)] || '';
    const success = await validar(v.id, nuevoEstatus, comentarioFila);
    if (success) {
      setValidaciones(prev => prev.map(prod =>
        prod.id === v.id
          ? { ...prod, validado: nuevoEstatus === 'APROBADO', comentarios: comentarioFila }
          : prod
      ));
      setComentarios(prev => ({ ...prev, [String(v.id)]: '' }));
      setModalOpen(false);
    }
  };

  // Eliminar referencias a validacionesFiltradas y adaptar la tabla para usar resumenCalculado
  // Mostrar la tabla de resumen por empresa calculada en tiempo real
  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Validación Operacional</h1>

      {/* Información del usuario actual */}
      {user && (
        <div className="mb-4 p-3 bg-blue-50 rounded border">
          <span className="text-sm text-blue-700">
            Usuario: <strong>{user.nombre}</strong> | 
            Rol: <strong>{user.rol}</strong>
            {canEditValidated() && <span className="text-green-600"> (Puede editar validaciones)</span>}
          </span>
        </div>
      )}

      {fetchError && (
        <div className="mb-4 p-4 bg-red-100 text-red-700 rounded border border-red-300">
          <strong>Error:</strong> {fetchError}
        </div>
      )}
      <div className="flex flex-wrap gap-4 mb-6 items-end">
        <div>
          <label className="block text-xs font-medium mb-1">Decena</label>
          <select
            className="border rounded px-2 py-1"
            value={decenaSeleccionada}
            onChange={e => setDecenaSeleccionada(e.target.value)}
          >
            <option value="">Todas</option>
            {generarOpcionesDecenas().map(decena => (
              <option key={decena} value={decena}>{getNombreDecena(decena)}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">Desde</label>
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={fechaDesde}
            onChange={e => setFechaDesde(e.target.value)}
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">Hasta</label>
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={fechaHasta}
            onChange={e => setFechaHasta(e.target.value)}
          />
        </div>
      </div>
      <div className="mb-6">
        <span className="text-lg font-semibold text-blue-700">Ganancia total (calculada en tiempo real): </span>
        <span className="text-2xl font-bold text-green-700">${gananciaTotal.toLocaleString(undefined, { maximumFractionDigits: 2 })}</span>
      </div>
      <table className="min-w-full border divide-y divide-gray-200 text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-2">Decena</th>
            <th className="p-2">Empresario</th>
            <th className="p-2">Ganancia</th>
            <th className="p-2">Estado</th>
            <th className="p-2">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {resumenCalculado.map((r, idx) => (
            <tr key={r.emp}>
              <td className="p-2">{decenaSeleccionada}</td>
              <td className="p-2">{r.emp}</td>
              <td className="p-2">${r.totalGanancia.toLocaleString(undefined, { maximumFractionDigits: 2 })}</td>
              <td className="p-2">
                {(() => {
                  const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(r.emp) && v.decena === decenaSeleccionada);
                  if (v) {
                    return v.validado ? <span className="text-green-600 font-semibold">Aprobado</span> : <span className="text-yellow-600 font-semibold">Pendiente</span>;
                  } else {
                    return <span className="text-gray-500">Sin validación</span>;
                  }
                })()}
              </td>
              <td className="p-2">
                <button
                  className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                  onClick={() => handleAbrirModal(idx)}
                >
                  Cambiar estado
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-4 text-xs text-gray-500">* Tras aprobar o rechazar, la edición queda bloqueada.</div>

      {/* Modal de cambio de estatus */}
      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)} title="Cambiar estatus de producción">
        <div className="p-4">
          <h2 className="text-lg font-bold mb-2">Cambiar estatus de producción</h2>
          <div className="mb-2">
            <label className="block text-sm font-medium mb-1">Nuevo estatus</label>
            <select
              className="border rounded px-2 py-1 w-full"
              value={nuevoEstatus}
              onChange={e => setNuevoEstatus(e.target.value)}
              disabled={(() => {
                if (modalIndex === null || !resumenCalculado[modalIndex]?.emp) return false;
                const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[modalIndex].emp) && v.decena === decenaSeleccionada);
                // Solo deshabilitar si está APROBADO y el usuario no es ADMIN
                return v?.validado && user?.rol !== 'ADMIN';
              })()}
            >
              {opcionesEstatus.map(opt => (
                <option key={opt} value={opt}>{opt}</option>
              ))}
            </select>
          </div>
          <div className="mb-2">
            <label className="block text-sm font-medium mb-1">Comentario</label>
            <textarea
              className="border rounded px-2 py-1 w-full"
              value={modalIndex !== null && resumenCalculado[modalIndex]?.emp ? comentarios[String(validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[modalIndex].emp) && v.decena === decenaSeleccionada)?.id)] || '' : ''}
              onChange={e => {
                if (modalIndex !== null && resumenCalculado[modalIndex]?.emp) {
                  const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[modalIndex].emp) && v.decena === decenaSeleccionada);
                  if (v) {
                    setComentarios(prev => ({ ...prev, [String(v.id)]: e.target.value }));
                  }
                }
              }}
              rows={3}
              disabled={(() => {
                // Solo deshabilitar si está APROBADO y el usuario no es ADMIN
                if (modalIndex === null || !resumenCalculado[modalIndex]?.emp) return false;
                const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[modalIndex].emp) && v.decena === decenaSeleccionada);
                return v?.validado && user?.rol !== 'ADMIN';
              })()}
            />
          </div>
          <div className="flex gap-2 justify-end mt-4">
            <button className="px-3 py-1 rounded bg-gray-200" onClick={() => setModalOpen(false)}>Cancelar</button>
            <button
              className="px-3 py-1 rounded bg-blue-600 text-white hover:bg-blue-700"
              onClick={handleGuardarModal}
              disabled={(() => {
                if (modalIndex === null || !resumenCalculado[modalIndex]?.emp) return false;
                const v = validaciones.find(v => normalize(v.entrepreneur?.nombre) === normalize(resumenCalculado[modalIndex].emp) && v.decena === decenaSeleccionada);
                return v?.validado && user?.rol !== 'ADMIN';
              })()}
            >
              Guardar
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
}
