import React, { useState, useEffect } from 'react';
import { tripsApi } from '../../services/api';

interface Trip {
  id: number;
  origin: string;
  destination: string;
  companyName: string;
  branchRevenue?: number;
  roadRevenue?: number;
  manualIncome?: string | number;
  totalIngresos: number;
  travelDate?: string;
}

interface Route {
  id: number;
  origen: string;
  destino: string;
  kilometraje: number;
  zona: { id: number; nombre: string; porcentaje: number };
}

interface Zone {
  id: number;
  nombre: string;
  porcentaje: number;
}

interface Production {
  id: number;
  decena: string;
  total: number;
  validado: boolean;
  comentarios: string;
  entrepreneur: { nombre: string };
}

interface DetalleZona {
  zona: string;
  porcentaje: number;
  servicios: number;
  ingresos: number;
  ganancia: number;
}

interface DetalleProduccion {
  empresa: string;
  decena: string;
  totalIngresos: number;
  totalGanancia: number;
  zonas: DetalleZona[];
}

// Función para normalizar strings (idéntica a backend, compatible universalmente)
function normalize(str: string): string {
  if (!str) return '';
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '') // Eliminar tildes
    .toLowerCase()
    .replace(/\s+/g, ' ')
    .trim();
}

// Función para obtener la decena actual
function getDecenaActual(): string {
  const ahora = new Date();
  const mes = ahora.getMonth() + 1; // getMonth() devuelve 0-11
  const año = ahora.getFullYear();
  const dia = ahora.getDate();
  
  // Determinar qué decena estamos
  let decena = 1;
  if (dia > 20) decena = 3;
  else if (dia > 10) decena = 2;
  
  return `${decena}${mes.toString().padStart(2, '0')}${año}`;
}

// Función para obtener fechas de una decena
function getFechasDecena(decenaStr: string): { desde: string; hasta: string } {
  const decena = parseInt(decenaStr.charAt(0));
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  
  let diaInicio = 1;
  let diaFin = 10;
  
  if (decena === 2) {
    diaInicio = 11;
    diaFin = 20;
  } else if (decena === 3) {
    diaInicio = 21;
    // Obtener el último día del mes (0 significa último día del mes anterior)
    const ultimoDia = new Date(año, mes, 0).getDate();
    diaFin = ultimoDia;
  }
  
  const desde = `${año}-${mes.toString().padStart(2, '0')}-${diaInicio.toString().padStart(2, '0')}`;
  const hasta = `${año}-${mes.toString().padStart(2, '0')}-${diaFin.toString().padStart(2, '0')}`;
  
  return { desde, hasta };
}

// Función para generar opciones de decenas (últimos 12 meses)
function generarOpcionesDecenas(): string[] {
  const opciones: string[] = [];
  const ahora = new Date();
  
  for (let i = 0; i < 12; i++) {
    const fecha = new Date(ahora.getFullYear(), ahora.getMonth() - i, 1);
    const mes = fecha.getMonth() + 1;
    const año = fecha.getFullYear();
    
    // Agregar las 3 decenas del mes
    opciones.push(`1${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`2${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`3${mes.toString().padStart(2, '0')}${año}`);
  }
  
  return opciones;
}

// Función para obtener el nombre del mes y año de una decena
function getNombreMesDecena(decenaStr: string): string {
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  const fecha = new Date(año, mes - 1, 1); // mes - 1 porque getMonth() devuelve 0-11
  return fecha.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' });
}

const CalculoProduccion: React.FC = () => {
  const [producciones, setProducciones] = useState<Production[]>([]);
  const [decenaSeleccionada, setDecenaSeleccionada] = useState<string>(getDecenaActual());
  const [loading, setLoading] = useState<boolean>(false);
  const [detalle, setDetalle] = useState<DetalleProduccion | null>(null);
  const [detalleLoading, setDetalleLoading] = useState<boolean>(false);
  const [detalleError, setDetalleError] = useState<string | null>(null);

  useEffect(() => {
    setLoading(true);
    fetch('/api/productions')
      .then(res => res.json())
      .then(data => setProducciones(data))
      .finally(() => setLoading(false));
  }, []);

  const opcionesDecenas = generarOpcionesDecenas();
  const produccionesFiltradas = producciones.filter(p => p.decena === decenaSeleccionada);

  const handleVerDetalle = async (empresa: string) => {
    setDetalle(null);
    setDetalleError(null);
    setDetalleLoading(true);
    try {
      const res = await fetch(`/api/productions/detalle?decena=${encodeURIComponent(decenaSeleccionada)}&empresa=${encodeURIComponent(empresa)}`);
      if (!res.ok) throw new Error('Error al obtener detalle');
      const data = await res.json();
      setDetalle(data);
    } catch (e: any) {
      setDetalleError(e.message);
    } finally {
      setDetalleLoading(false);
    }
  };

  const handleCerrarDetalle = () => {
    setDetalle(null);
    setDetalleError(null);
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Producción por Decena</h1>
      <div className="mb-4">
        <label className="mr-2 font-semibold">Decena:</label>
        <select
          value={decenaSeleccionada}
          onChange={e => setDecenaSeleccionada(e.target.value)}
          className="border rounded px-2 py-1"
        >
          {opcionesDecenas.map(dec => (
            <option key={dec} value={dec}>{dec}</option>
          ))}
        </select>
      </div>
      {loading ? (
        <div>Cargando producciones...</div>
      ) : (
        <table className="min-w-full bg-white rounded shadow">
          <thead>
            <tr className="bg-blue-100">
              <th className="p-2">Decena</th>
              <th className="p-2">Empresa</th>
              <th className="p-2">Total Transportado</th>
              <th className="p-2">Ganancia</th>
              <th className="p-2">Ver detalle</th>
            </tr>
          </thead>
          <tbody>
            {produccionesFiltradas.map((p, i) => (
              <tr key={p.id} className="border-t">
                <td className="p-2">{p.decena}</td>
                <td className="p-2">{p.entrepreneur?.nombre || '-'}</td>
                <td className="p-2">{/* Se obtiene del detalle */} <DetalleIngresos empresa={p.entrepreneur?.nombre || ''} decena={p.decena} /> </td>
                <td className="p-2">${p.total?.toLocaleString()}</td>
                <td className="p-2">
                  <button className="bg-blue-600 text-white px-3 py-1 rounded" onClick={() => handleVerDetalle(p.entrepreneur?.nombre || '')}>
                    Ver detalle
                  </button>
                </td>
              </tr>
            ))}
            {produccionesFiltradas.length === 0 && (
              <tr><td colSpan={5} className="text-center text-gray-400 p-4">No hay datos para mostrar.</td></tr>
            )}
          </tbody>
        </table>
      )}
      {/* Modal de detalle */}
      {detalle && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded shadow-lg p-8 max-w-2xl w-full relative">
            <button className="absolute top-2 right-2 text-gray-500 hover:text-gray-800" onClick={handleCerrarDetalle}>✕</button>
            <h2 className="text-xl font-bold mb-4">Detalle de Producción<br />{detalle.empresa} - Decena {detalle.decena}</h2>
            <div className="mb-2"><b>Total transportado:</b> ${detalle.totalIngresos.toLocaleString()}</div>
            <div className="mb-4"><b>Ganancia total:</b> ${detalle.totalGanancia.toLocaleString()}</div>
            <table className="min-w-full bg-white rounded shadow mb-4">
              <thead>
                <tr className="bg-blue-50">
                  <th className="p-2">Zona</th>
                  <th className="p-2">% Zona</th>
                  <th className="p-2">Servicios</th>
                  <th className="p-2">Ingresos</th>
                  <th className="p-2">Ganancia</th>
                </tr>
              </thead>
              <tbody>
                {detalle.zonas.map((z, idx) => (
                  <tr key={idx}>
                    <td className="p-2">{z.zona}</td>
                    <td className="p-2">{z.porcentaje}%</td>
                    <td className="p-2">{z.servicios}</td>
                    <td className="p-2">${z.ingresos.toLocaleString()}</td>
                    <td className="p-2">${z.ganancia.toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
      {detalleLoading && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded shadow-lg p-8 max-w-md w-full text-center">
            <div className="mb-4">Cargando detalle...</div>
            <button className="mt-2 px-4 py-2 bg-blue-600 text-white rounded" onClick={handleCerrarDetalle}>Cerrar</button>
          </div>
        </div>
      )}
      {detalleError && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded shadow-lg p-8 max-w-md w-full text-center">
            <div className="mb-4 text-red-600">{detalleError}</div>
            <button className="mt-2 px-4 py-2 bg-blue-600 text-white rounded" onClick={handleCerrarDetalle}>Cerrar</button>
          </div>
        </div>
      )}
    </div>
  );
};

// Componente auxiliar para mostrar ingresos totales en la tabla principal
const DetalleIngresos: React.FC<{ empresa: string; decena: string }> = ({ empresa, decena }) => {
  const [ingresos, setIngresos] = useState<number | null>(null);
  useEffect(() => {
    let mounted = true;
    fetch(`/api/productions/detalle?decena=${encodeURIComponent(decena)}&empresa=${encodeURIComponent(empresa)}`)
      .then(res => res.json())
      .then(data => { if (mounted) setIngresos(data.totalIngresos); })
      .catch(() => { if (mounted) setIngresos(null); });
    return () => { mounted = false; };
  }, [empresa, decena]);
  return <span>{ingresos !== null ? `$${ingresos.toLocaleString()}` : '-'}</span>;
};

export default CalculoProduccion;
