import { useState, useEffect } from 'react';

export interface User {
  id: number;
  username: string;
  role: 'ADMIN' | 'VALIDADOR' | 'MIEMBRO' | 'INVITADO';
}

export function useAuth() {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simular obtención del usuario actual
    // En una implementación real, esto vendría de tu sistema de autenticación
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    }
    setLoading(false);
  }, []);

  const isAdmin = () => user?.role === 'ADMIN';
  const isValidador = () => user?.role === 'VALIDADOR' || user?.role === 'ADMIN';
  const canEditValidated = () => user?.role === 'ADMIN';

  const login = (userData: User) => {
    setUser(userData);
    localStorage.setItem('currentUser', JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('currentUser');
  };

  return {
    user,
    loading,
    isAdmin,
    isValidador,
    canEditValidated,
    login,
    logout,
  };
}
