import React, { useState, useEffect } from 'react';

interface Zone {
  id?: number;
  nombre: string;
  porcentaje: number;
}

const RegistroRecorridos: React.FC = () => {
  const [zones, setZones] = useState<Zone[]>([]);
  const [nombre, setNombre] = useState('');
  const [porcentaje, setPorcentaje] = useState<number>(0);
  const [editId, setEditId] = useState<number | null>(null);

  // Cargar zonas al montar
  useEffect(() => {
    fetch('/api/zones')
      .then(res => res.json())
      .then(data => setZones(data))
      .catch(err => console.error('Error cargando zonas:', err));
  }, []);

  // Crear o editar zona
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!nombre || porcentaje <= 0) return;
    const zona = { nombre, porcentaje };
    if (editId) {
      // Editar
      const res = await fetch(`/api/zones/${editId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(zona),
      });
      if (res.ok) {
        const zonaEditada = await res.json();
        setZones(zones.map(z => (z.id === editId ? zonaEditada : z)));
        setEditId(null);
        setNombre('');
        setPorcentaje(0);
      }
    } else {
      // Crear
      const res = await fetch('/api/zones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(zona),
      });
      if (res.ok) {
        const zonaCreada = await res.json();
        setZones([...zones, zonaCreada]);
        setNombre('');
        setPorcentaje(0);
      }
    }
  };

  // Eliminar zona
  const handleDelete = async (id?: number) => {
    if (!id) return;
    if (!window.confirm('¿Seguro que deseas eliminar esta zona?')) return;
    const res = await fetch(`/api/zones/${id}`, { method: 'DELETE' });
    if (res.ok) {
      setZones(zones.filter(z => z.id !== id));
    }
  };

  // Editar zona (cargar en formulario)
  const handleEdit = (zone: Zone) => {
    setEditId(zone.id!);
    setNombre(zone.nombre);
    setPorcentaje(zone.porcentaje);
  };

  // Cancelar edición
  const handleCancelEdit = () => {
    setEditId(null);
    setNombre('');
    setPorcentaje(0);
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Manejo de Zonas</h1>
      <form onSubmit={handleSubmit} className="mb-6 flex flex-col md:flex-row gap-4 items-end">
        <div>
          <label className="block text-sm font-medium">Nombre de la zona</label>
          <input
            type="text"
            value={nombre}
            onChange={e => setNombre(e.target.value)}
            className="border px-2 py-1 rounded w-full"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Porcentaje (%)</label>
          <input
            type="number"
            value={porcentaje}
            onChange={e => setPorcentaje(Number(e.target.value))}
            className="border px-2 py-1 rounded w-full"
            min={0}
            step={0.01}
            required
          />
        </div>
        <div>
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
            {editId ? 'Actualizar' : 'Agregar'}
          </button>
          {editId && (
            <button type="button" onClick={handleCancelEdit} className="ml-2 px-4 py-2 rounded bg-gray-400 text-white">Cancelar</button>
          )}
        </div>
      </form>
      <table className="min-w-full border divide-y divide-gray-200 text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left">Nombre</th>
            <th className="px-4 py-2 text-left">Porcentaje (%)</th>
            <th className="px-4 py-2 text-left">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {zones.map(zone => (
            <tr key={zone.id} className="border-b">
              <td className="px-4 py-2">{zone.nombre}</td>
              <td className="px-4 py-2">{zone.porcentaje}</td>
              <td className="px-4 py-2">
                <button
                  className="bg-yellow-500 text-white px-2 py-1 rounded mr-2"
                  onClick={() => handleEdit(zone)}
                >
                  Editar
                </button>
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded"
                  onClick={() => handleDelete(zone.id)}
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RegistroRecorridos;
