import React, { useState } from 'react';
import { useReporteGlobalSummary, useReporteEmpresarioDetallado, useReporteZonaDetallada, useReporteProduccionLiquidaciones } from '../../hooks/useReportesApi';

const REPORTES = [
  { key: 'global', label: 'Resumen Global' },
  { key: 'empresario', label: 'Detallado por Empresario' },
  { key: 'zona', label: 'Detallado por Zona' },
  { key: 'produccion', label: 'Producción y Liquidaciones' },
  { key: 'validaciones', label: 'Validaciones y Aprobaciones' },
  { key: 'auditoria', label: 'Auditoría de Cambios' },
];

export default function ReportesGenerales() {
  const [tipoReporte, setTipoReporte] = useState('global');
  // Filtros globales
  const [desde, setDesde] = useState('2024-07-01');
  const [hasta, setHasta] = useState('2024-07-10');
  // Empresario
  const [empresarioId, setEmpresarioId] = useState<number>(0);
  const empresariosList = [
    { id: 1, nombre: 'Empresa 1' },
    { id: 2, nombre: 'Empresa 2' },
    { id: 3, nombre: 'Empresa 3' },
  ];
  // Zona
  const [zona, setZona] = useState('');
  const zonasList = ['JB', 'Norte Largo', 'Centro', 'Sur'];
  // Decenas simuladas (en real, cargar desde backend)
  const decenasList = [
    { label: '1ª Decena Julio 2024', desde: '2024-07-01', hasta: '2024-07-10' },
    { label: '2ª Decena Julio 2024', desde: '2024-07-11', hasta: '2024-07-20' },
    { label: '3ª Decena Julio 2024', desde: '2024-07-21', hasta: '2024-07-31' },
  ];
  const [decena, setDecena] = useState('');

  // Hooks de reportes
  const { data, loading, error, fetchSummary } = useReporteGlobalSummary();
  const { data: dataEmp, loading: loadingEmp, error: errorEmp, fetchEmpresario } = useReporteEmpresarioDetallado();
  const { data: dataZona, loading: loadingZona, error: errorZona, fetchZona } = useReporteZonaDetallada();
  const { data: dataProd, loading: loadingProd, error: errorProd, fetchProduccion } = useReporteProduccionLiquidaciones();

  // Handlers
  const handleBuscarResumen = () => fetchSummary(desde, hasta);
  const handleBuscarEmpresario = () => empresarioId && fetchEmpresario(empresarioId, desde, hasta);
  const handleBuscarZona = () => zona && fetchZona(zona, desde, hasta);
  const handleBuscarProduccion = () => fetchProduccion(empresarioId || null, desde, hasta);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Reportes</h1>
      {/* Selector de decena y fechas global para todos los reportes */}
      <div className="mb-4 flex flex-wrap gap-4 items-end">
        <div>
          <label className="block text-xs text-gray-600">Decena</label>
          <select
            className="border rounded px-2 py-2 w-[160px] h-[38px]"
            value={decena}
            onChange={e => {
              setDecena(e.target.value);
              const found = decenasList.find(d => d.label === e.target.value);
              if (found) {
                setDesde(found.desde);
                setHasta(found.hasta);
              }
            }}
          >
            <option value="">Seleccionar decena</option>
            {decenasList.map(d => <option key={d.label} value={d.label}>{d.label}</option>)}
          </select>
        </div>
        <div>
          <label className="block text-xs text-gray-600">Desde</label>
          <input type="date" value={desde} onChange={e => setDesde(e.target.value)} className="border rounded px-2 py-1" />
        </div>
        <div>
          <label className="block text-xs text-gray-600">Hasta</label>
          <input type="date" value={hasta} onChange={e => setHasta(e.target.value)} className="border rounded px-2 py-1" />
        </div>
      </div>
      <div className="mb-6 flex flex-wrap gap-4 items-end">
        <label className="font-semibold">Tipo de Reporte:</label>
        <select className="border p-2" value={tipoReporte} onChange={e => setTipoReporte(e.target.value)}>
          {REPORTES.map(r => <option key={r.key} value={r.key}>{r.label}</option>)}
        </select>
      </div>
      {/* Filtros y visualización dinámica */}
      {tipoReporte === 'global' && (
        <div className="bg-blue-50 rounded-lg p-4 mb-6 flex flex-col md:flex-row md:items-end gap-4">
          <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarResumen} disabled={loading || !desde || !hasta}>
            {loading ? 'Cargando...' : 'Buscar Resumen'}
          </button>
          {error && <span className="text-red-600 text-sm ml-4">{error}</span>}
          {data && (
            <div className="flex flex-wrap gap-6 ml-6">
              <div className="text-center">
                <div className="text-xs text-gray-500">Recorridos</div>
                <div className="text-xl font-bold">{data.totalRecorridos}</div>
              </div>
              <div className="text-center">
                <div className="text-xs text-gray-500">Ingresos</div>
                <div className="text-xl font-bold">${data.totalIngresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</div>
              </div>
              <div className="text-center">
                <div className="text-xs text-gray-500">Empresarios</div>
                <div className="text-xl font-bold">{data.totalEmpresarios}</div>
              </div>
              <div className="text-center">
                <div className="text-xs text-gray-500">Zonas</div>
                <div className="text-xl font-bold">{data.totalZonas}</div>
              </div>
            </div>
          )}
        </div>
      )}

      {tipoReporte === 'empresario' && (
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <h2 className="text-lg font-bold mb-2">Reporte Detallado por Empresario</h2>
          <div className="flex flex-wrap gap-4 items-end mb-4">
            <select className="border p-2" value={empresarioId} onChange={e => setEmpresarioId(Number(e.target.value))}>
              <option value={0}>Seleccione empresario</option>
              {empresariosList.map(e => <option key={e.id} value={e.id}>{e.nombre}</option>)}
            </select>
            <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarEmpresario} disabled={loadingEmp || !desde || !hasta || !empresarioId}>
              {loadingEmp ? 'Cargando...' : 'Buscar'}
            </button>
            {errorEmp && <span className="text-red-600 text-sm ml-4">{errorEmp}</span>}
          </div>
          {dataEmp && !dataEmp.error && (
            <div>
              <div className="mb-2 font-semibold">Empresario: <span className="text-blue-700">{dataEmp.empresario}</span></div>
              <div className="flex flex-wrap gap-8 mb-4">
                <div>Recorridos: <b>{dataEmp.totalRecorridos}</b></div>
                <div>Ingresos: <b>${dataEmp.totalIngresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</b></div>
                <div>Zonas: <b>{(dataEmp.zonas || []).join(', ')}</b></div>
              </div>
              <div>
                <h3 className="font-bold mb-1">Evolución por decena</h3>
                <table className="min-w-[300px] bg-gray-50 rounded">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="p-2">Decena</th>
                      <th className="p-2">Recorridos</th>
                      <th className="p-2">Ingresos</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.entries(dataEmp.evolucionPorDecena || {}).map(([decena, evo]: any) => (
                      <tr key={decena}>
                        <td className="p-2">{decena}</td>
                        <td className="p-2">{evo.recorridos}</td>
                        <td className="p-2">${evo.ingresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          {dataEmp && dataEmp.error && (
            <div className="text-red-600 text-sm mt-2">{dataEmp.error}</div>
          )}
        </div>
      )}

      {/* Aquí irán las secciones de los otros reportes, con sus filtros y visualización */}
      {tipoReporte === 'zona' && (
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <h2 className="text-lg font-bold mb-2">Reporte Detallado por Zona</h2>
          <div className="flex flex-wrap gap-4 items-end mb-4">
            <select className="border p-2" value={zona} onChange={e => setZona(e.target.value)}>
              <option value="">Seleccione zona</option>
              {zonasList.map(z => <option key={z} value={z}>{z}</option>)}
            </select>
            <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarZona} disabled={loadingZona || !zona || !desde || !hasta}>
              {loadingZona ? 'Cargando...' : 'Buscar'}
            </button>
            {errorZona && <span className="text-red-600 text-sm ml-4">{errorZona}</span>}
          </div>
          {dataZona && !dataZona.error && (
            <div>
              <div className="mb-2 font-semibold">Zona: <span className="text-blue-700">{dataZona.zona}</span></div>
              <div className="flex flex-wrap gap-8 mb-4">
                <div>Recorridos: <b>{dataZona.totalRecorridos}</b></div>
                <div>Ingresos: <b>${dataZona.totalIngresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</b></div>
                <div>Empresarios: <b>{(dataZona.empresarios || []).join(', ')}</b></div>
              </div>
              <div>
                <h3 className="font-bold mb-1">Ranking de Empresarios</h3>
                <table className="min-w-[300px] bg-gray-50 rounded">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="p-2">Empresario</th>
                      <th className="p-2">Ingresos</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dataZona.rankingEmpresarios && Object.entries(dataZona.rankingEmpresarios).sort((a: any, b: any) => b[1] - a[1]).map(([emp, ingresos]: any) => (
                      <tr key={emp}>
                        <td className="p-2">{emp}</td>
                        <td className="p-2">${Number(ingresos).toLocaleString(undefined, { maximumFractionDigits: 0 })}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          {dataZona && dataZona.error && (
            <div className="text-red-600 text-sm mt-2">{dataZona.error}</div>
          )}
        </div>
      )}
      {tipoReporte === 'produccion' && (
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <h2 className="text-lg font-bold mb-2">Producción y Liquidaciones</h2>
          <div className="flex flex-wrap gap-4 items-end mb-4">
            <select className="border p-2" value={empresarioId} onChange={e => setEmpresarioId(Number(e.target.value))}>
              <option value={0}>Todos los empresarios</option>
              {empresariosList.map(e => <option key={e.id} value={e.id}>{e.nombre}</option>)}
            </select>
            <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarProduccion} disabled={loadingProd || !desde || !hasta}>
              {loadingProd ? 'Cargando...' : 'Buscar'}
            </button>
            {errorProd && <span className="text-red-600 text-sm ml-4">{errorProd}</span>}
          </div>
          {dataProd && dataProd.produccionLiquidaciones && (
            <div className="overflow-x-auto">
              <table className="min-w-full bg-gray-50 rounded">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="p-2">Empresario</th>
                    <th className="p-2">Decena</th>
                    <th className="p-2">Producción</th>
                    <th className="p-2">Validado</th>
                    <th className="p-2">Liquidación</th>
                  </tr>
                </thead>
                <tbody>
                  {dataProd.produccionLiquidaciones.map((item: any, i: number) => (
                    <tr key={i}>
                      <td className="p-2">{item.empresario}</td>
                      <td className="p-2">{item.decena}</td>
                      <td className="p-2">${item.totalProduccion?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</td>
                      <td className="p-2">{item.validado ? 'Sí' : 'No'}</td>
                      <td className="p-2">
                        {item.liquidacion ? (
                          <div>
                            <div>Monto: ${item.liquidacion.monto?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</div>
                            <div>Estado: {item.liquidacion.estado}</div>
                            <div>Pago: {item.liquidacion.fechaPago || '-'}</div>
                            <div>Aprobación: {item.liquidacion.fechaAprobacion || '-'}</div>
                          </div>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
          {dataProd && dataProd.produccionLiquidaciones?.length === 0 && (
            <div className="text-gray-500 mt-4">No hay datos para mostrar.</div>
          )}
        </div>
      )}
      {tipoReporte === 'validaciones' && (
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <h2 className="text-lg font-bold mb-2">Validaciones y Aprobaciones</h2>
          <div className="text-gray-400">(Próximamente...)</div>
        </div>
      )}
      {tipoReporte === 'auditoria' && (
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <h2 className="text-lg font-bold mb-2">Auditoría de Cambios</h2>
          <div className="text-gray-400">(Próximamente...)</div>
        </div>
      )}
    </div>
  );
}
