import React, { useState, useEffect } from 'react';
import CsvImport from '../components/CsvImport';
import TripList from '../components/TripList';
import { tripsApi } from '../services/api';

interface Trip {
  id: number;
  travelDate: string;
  departureTime: string;
  origin: string;
  destination: string;
  routeName: string;
  serviceCode: string;
  serviceType: string;
  status: string;
  busNumber: string;
  licensePlate: string;
  vehicleYear: number;
  totalSeats: number;
  initialScore: number;
  additionalScore: number;
  totalScore: number;
  compensation: number;
  totalCompensated: number;
  companyRut: string;
  companyName: string;
  driverName: string;
  branchSeats: number;
  branchRevenue: number;
  roadSeats: number;
  roadRevenue: number;
  manualIncome: string;
}

const TripsPage: React.FC = () => {
  const [trips, setTrips] = useState<Trip[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showImport, setShowImport] = useState(false);
  const [fechaDesde, setFechaDesde] = useState<string>('');
  const [fechaHasta, setFechaHasta] = useState<string>('');

  // Cargar viajes al montar el componente
  useEffect(() => {
    fetchTrips();
  }, []);

  const fetchTrips = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const data = await tripsApi.getAll();
      setTrips(data);
    } catch (err) {
      setError('Error al cargar los viajes');
      console.error('Error fetching trips:', err);
    } finally {
      setLoading(false);
    }
  };

  // Filtrar viajes por fecha
  const tripsFiltrados = trips.filter(trip => {
    if (!trip.travelDate) return true; // Si no hay fecha, incluir
    
    const fechaViaje = new Date(trip.travelDate);
    // Normalizar la fecha del viaje a solo fecha (sin hora)
    const fechaViajeNormalizada = new Date(fechaViaje.getFullYear(), fechaViaje.getMonth(), fechaViaje.getDate());
    
    if (fechaDesde) {
      const desde = new Date(fechaDesde);
      if (fechaViajeNormalizada < desde) return false;
    }
    
    if (fechaHasta) {
      const hasta = new Date(fechaHasta);
      if (fechaViajeNormalizada > hasta) return false;
    }
    
    return true;
  });

  // Validar que la fecha "hasta" sea mayor o igual que "desde"
  const fechaValida = !fechaDesde || !fechaHasta || new Date(fechaDesde) <= new Date(fechaHasta);
  const mensajeError = fechaDesde && fechaHasta && !fechaValida 
    ? 'La fecha "Hasta" debe ser mayor o igual que la fecha "Desde"' 
    : '';

  const handleImportSuccess = (importedTrips: Trip[]) => {
    setTrips(importedTrips);
    setShowImport(false);
    alert(`Importación exitosa: ${importedTrips.length} viajes importados.`);
  };

  const handleImportError = (errorMessage: string) => {
    setError(errorMessage);
  };

  const handleTripSelect = (trip: Trip) => {
    // Aquí puedes implementar la lógica para mostrar detalles del viaje
    console.log('Viaje seleccionado:', trip);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Sistema de Transporte Pullman</h1>
          <p className="mt-2 text-gray-600">Gestión de viajes y análisis de ingresos</p>
        </div>

        {/* Botones de acción */}
        <div className="mb-6 flex flex-wrap gap-4">
          <button
            onClick={() => setShowImport(!showImport)}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
          >
            {showImport ? 'Ocultar Importación' : 'Importar CSV'}
          </button>
          
          <button
            onClick={fetchTrips}
            disabled={loading}
            className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors disabled:opacity-50"
          >
            {loading ? 'Cargando...' : 'Actualizar Datos'}
          </button>
        </div>

        {/* Filtros de fecha */}
        <div className="mb-6 bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-3">Filtros de Fecha</h3>
          <div className="flex gap-4 items-end">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Desde:
              </label>
              <input
                type="date"
                value={fechaDesde}
                onChange={(e) => setFechaDesde(e.target.value)}
                className={`border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  !fechaValida ? 'border-red-500' : 'border-gray-300'
                }`}
                max={fechaHasta || undefined}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Hasta:
              </label>
              <input
                type="date"
                value={fechaHasta}
                onChange={(e) => setFechaHasta(e.target.value)}
                className={`border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  !fechaValida ? 'border-red-500' : 'border-gray-300'
                }`}
                min={fechaDesde || undefined}
              />
            </div>
            <button
              onClick={() => {
                setFechaDesde('');
                setFechaHasta('');
              }}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
            >
              Limpiar filtros
            </button>
          </div>
          {mensajeError && (
            <p className="text-sm text-red-600 mt-2">{mensajeError}</p>
          )}
          {(fechaDesde || fechaHasta) && (
            <p className="text-sm text-gray-600 mt-2">
              Mostrando {fechaValida ? tripsFiltrados.length : 0} de {trips.length} viajes
              {fechaDesde && fechaHasta && (
                <span className="ml-2">
                  (Período: {new Date(fechaDesde).toLocaleDateString()} - {new Date(fechaHasta).toLocaleDateString()})
                </span>
              )}
            </p>
          )}
        </div>

        {/* Componente de importación */}
        {showImport && (
          <div className="mb-8">
            <CsvImport
              onImportSuccess={handleImportSuccess}
              onImportError={handleImportError}
            />
          </div>
        )}

        {/* Mensaje de error */}
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-md">
            <p className="text-red-700">{error}</p>
          </div>
        )}

        {/* Lista de viajes */}
        {loading ? (
          <div className="flex justify-center items-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            <span className="ml-3 text-gray-600">Cargando viajes...</span>
          </div>
        ) : (fechaValida && tripsFiltrados.length > 0) ? (
          <TripList trips={tripsFiltrados} onTripSelect={handleTripSelect} />
        ) : (
          <div className="text-center py-12">
            <div className="text-gray-400 mb-4">
              <svg className="mx-auto h-12 w-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {!fechaValida ? 'Rango de fechas inválido' : 
               trips.length > 0 ? 'No hay viajes en el rango de fechas seleccionado' : 'No hay viajes registrados'}
            </h3>
            <p className="text-gray-500 mb-4">
              {!fechaValida ? 'Corrige el rango de fechas seleccionado.' :
               trips.length > 0 
                ? 'Ajusta los filtros de fecha o importa más datos.'
                : 'Importa un archivo CSV para comenzar a gestionar los viajes.'
              }
            </p>
            <button
              onClick={() => setShowImport(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              Importar CSV
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default TripsPage;
