import React, { useState, useEffect } from 'react';

interface Production {
  id: number;
  decena: string;
  total: number;
  validado: boolean;
  comentarios: string;
  entrepreneur: { nombre: string };
}

function getDecenaActual(): string {
  const ahora = new Date();
  const mes = ahora.getMonth() + 1;
  const año = ahora.getFullYear();
  const dia = ahora.getDate();
  let decena = 1;
  if (dia > 20) decena = 3;
  else if (dia > 10) decena = 2;
  return `${decena}${mes.toString().padStart(2, '0')}${año}`;
}

function getFechasDecena(decenaStr: string): { desde: string; hasta: string } {
  const decena = parseInt(decenaStr.charAt(0));
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  let diaInicio = 1;
  let diaFin = 10;
  if (decena === 2) {
    diaInicio = 11;
    diaFin = 20;
  } else if (decena === 3) {
    diaInicio = 21;
    const ultimoDia = new Date(año, mes, 0).getDate();
    diaFin = ultimoDia;
  }
  const desde = `${año}-${mes.toString().padStart(2, '0')}-${diaInicio.toString().padStart(2, '0')}`;
  const hasta = `${año}-${mes.toString().padStart(2, '0')}-${diaFin.toString().padStart(2, '0')}`;
  return { desde, hasta };
}

function generarOpcionesDecenas(): string[] {
  const opciones: string[] = [];
  const ahora = new Date();
  for (let i = 0; i < 12; i++) {
    const fecha = new Date(ahora.getFullYear(), ahora.getMonth() - i, 1);
    const mes = fecha.getMonth() + 1;
    const año = fecha.getFullYear();
    opciones.push(`1${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`2${mes.toString().padStart(2, '0')}${año}`);
    opciones.push(`3${mes.toString().padStart(2, '0')}${año}`);
  }
  return opciones;
}

function getNombreMesDecena(decenaStr: string): string {
  const mes = parseInt(decenaStr.substring(1, 3));
  const año = parseInt(decenaStr.substring(3));
  const fecha = new Date(año, mes - 1, 1);
  return fecha.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' });
}

const CalculoProduccion: React.FC = () => {
  const [producciones, setProducciones] = useState<Production[]>([]);
  const [decenaSeleccionada, setDecenaSeleccionada] = useState<string>(getDecenaActual());
  const [fechaDesde, setFechaDesde] = useState<string>('');
  const [fechaHasta, setFechaHasta] = useState<string>('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (decenaSeleccionada) {
      const { desde, hasta } = getFechasDecena(decenaSeleccionada);
      setFechaDesde(desde);
      setFechaHasta(hasta);
    }
  }, [decenaSeleccionada]);

  useEffect(() => {
    const fetchProducciones = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch('/api/productions');
        if (!res.ok) throw new Error('Error al obtener producciones');
        const data = await res.json();
        setProducciones(data);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProducciones();
  }, []);

  // Filtrar por decena seleccionada
  const produccionesFiltradas = producciones.filter(p => p.decena === decenaSeleccionada);
  const gananciaTotal = produccionesFiltradas.reduce((acc, p) => acc + (p.total || 0), 0);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Producción por Empresario</h1>
      <div className="bg-gray-50 p-4 rounded-lg mb-6">
        <h3 className="text-lg font-semibold mb-3">Filtros por Decena</h3>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Seleccionar Decena:
          </label>
          <select
            value={decenaSeleccionada}
            onChange={(e) => setDecenaSeleccionada(e.target.value)}
            className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {generarOpcionesDecenas().map(decena => {
                const decenaNum = decena.charAt(0);
                const nombreMesAño = getNombreMesDecena(decena);
                return (
                  <option key={decena} value={decena}>
                    {decenaNum}ª Decena {nombreMesAño}
                  </option>
                );
              })}
          </select>
        </div>
        <div className="text-sm text-gray-600 mb-3">
          <strong>Período seleccionado:</strong> {fechaDesde && fechaHasta ? 
            `${new Date(fechaDesde).toLocaleDateString()} - ${new Date(fechaHasta).toLocaleDateString()}` : 
            'No seleccionado'
          }
        </div>
      </div>
      {loading ? (
        <div className="text-blue-600">Cargando producciones...</div>
      ) : error ? (
        <div className="text-red-600">{error}</div>
      ) : (
        <>
          <div className="mb-6">
            <span className="text-lg font-semibold text-blue-700">Ganancia total: </span>
            <span className="text-2xl font-bold text-green-700">${gananciaTotal.toLocaleString()}</span>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white rounded shadow">
              <thead>
                <tr className="bg-blue-100">
                  <th className="p-2">Decena</th>
                  <th className="p-2">Empresario</th>
                  <th className="p-2">Ganancia</th>
                  <th className="p-2">Estado</th>
                  <th className="p-2">Comentarios</th>
                </tr>
              </thead>
              <tbody>
                {produccionesFiltradas.map((p, i) => (
                  <tr key={i} className="border-t">
                    <td className="p-2">{p.decena}</td>
                    <td className="p-2">{p.entrepreneur?.nombre || '-'}</td>
                    <td className="p-2">${p.total?.toLocaleString() ?? 0}</td>
                    <td className="p-2">
                      {p.validado ? <span className="text-green-600 font-semibold">Aprobado</span> : <span className="text-yellow-600 font-semibold">Pendiente</span>}
                    </td>
                    <td className="p-2">{p.comentarios}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default CalculoProduccion;
