import React, { useState } from 'react';
import { useReporteGlobalSummary } from '../../hooks/useReportesApi';
import { useReporteEmpresarioDetallado } from '../../hooks/useReportesApi';

const decenas = ['2024-07-01 al 2024-07-10', '2024-07-11 al 2024-07-20'];
const zonas = ['JB', 'Norte Largo', 'Centro', 'Sur'];
const empresarios = ['Empresa 1', 'Empresa 2', 'Empresa 3'];

const datos = [
  { decena: decenas[0], zona: 'JB', empresario: 'Empresa 1', recorridos: 100, monto: 20000 },
  { decena: decenas[0], zona: 'Norte Largo', empresario: 'Empresa 2', recorridos: 80, monto: 16800 },
  { decena: decenas[1], zona: 'Centro', empresario: 'Empresa 3', recorridos: 120, monto: 22800 },
];

export default function ReportesGenerales() {
  const [filtroDecena, setFiltroDecena] = useState('');
  const [filtroZona, setFiltroZona] = useState('');
  const [filtroEmpresario, setFiltroEmpresario] = useState('');
  const [desde, setDesde] = useState('2024-07-01');
  const [hasta, setHasta] = useState('2024-07-10');
  const { data, loading, error, fetchSummary } = useReporteGlobalSummary();
  const { data: dataEmp, loading: loadingEmp, error: errorEmp, fetchEmpresario } = useReporteEmpresarioDetallado();
  const [empresarioId, setEmpresarioId] = useState<number>(0);
  // SimulaciÃ³n de IDs de empresarios (en real, cargar desde backend)
  const empresariosList = [
    { id: 1, nombre: 'Empresa 1' },
    { id: 2, nombre: 'Empresa 2' },
    { id: 3, nombre: 'Empresa 3' },
  ];

  const filtrados = datos.filter(d =>
    (!filtroDecena || d.decena === filtroDecena) &&
    (!filtroZona || d.zona === filtroZona) &&
    (!filtroEmpresario || d.empresario === filtroEmpresario)
  );

  const handleExport = (tipo: 'pdf' | 'excel') => {
    alert(`Exportando reporte a ${tipo.toUpperCase()} (simulado)`);
  };

  const handleBuscarResumen = () => {
    fetchSummary(desde, hasta);
  };

  const handleBuscarEmpresario = () => {
    if (empresarioId) fetchEmpresario(empresarioId, desde, hasta);
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Reportes Generales</h1>
      {/* Resumen Global */}
      <div className="bg-blue-50 rounded-lg p-4 mb-6 flex flex-col md:flex-row md:items-end gap-4">
        <div>
          <label className="block text-xs text-gray-600">Desde</label>
          <input type="date" value={desde} onChange={e => setDesde(e.target.value)} className="border rounded px-2 py-1" />
        </div>
        <div>
          <label className="block text-xs text-gray-600">Hasta</label>
          <input type="date" value={hasta} onChange={e => setHasta(e.target.value)} className="border rounded px-2 py-1" />
        </div>
        <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarResumen} disabled={loading}>
          {loading ? 'Cargando...' : 'Buscar Resumen'}
        </button>
        {error && <span className="text-red-600 text-sm ml-4">{error}</span>}
        {data && (
          <div className="flex flex-wrap gap-6 ml-6">
            <div className="text-center">
              <div className="text-xs text-gray-500">Recorridos</div>
              <div className="text-xl font-bold">{data.totalRecorridos}</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Ingresos</div>
              <div className="text-xl font-bold">${data.totalIngresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Empresarios</div>
              <div className="text-xl font-bold">{data.totalEmpresarios}</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Zonas</div>
              <div className="text-xl font-bold">{data.totalZonas}</div>
            </div>
          </div>
        )}
      </div>
      {/* Filtros y tabla de ejemplo (mantener para referencia) */}
      <div className="flex flex-wrap gap-4 mb-4">
        <select className="border p-2" value={filtroDecena} onChange={e => setFiltroDecena(e.target.value)}>
          <option value="">Todas las decenas</option>
          {decenas.map(d => <option key={d}>{d}</option>)}
        </select>
        <select className="border p-2" value={filtroZona} onChange={e => setFiltroZona(e.target.value)}>
          <option value="">Todas las zonas</option>
          {zonas.map(z => <option key={z}>{z}</option>)}
        </select>
        <select className="border p-2" value={filtroEmpresario} onChange={e => setFiltroEmpresario(e.target.value)}>
          <option value="">Todos los empresarios</option>
          {empresarios.map(e => <option key={e}>{e}</option>)}
        </select>
        <button className="bg-green-600 text-white px-4 py-2 rounded" onClick={() => handleExport('pdf')}>Exportar PDF</button>
        <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={() => handleExport('excel')}>Exportar Excel</button>
      </div>
      {/* Reporte Detallado por Empresario */}
      <div className="bg-white rounded-lg shadow p-4 mb-8 mt-8">
        <h2 className="text-lg font-bold mb-2">Reporte Detallado por Empresario</h2>
        <div className="flex flex-wrap gap-4 items-end mb-4">
          <select className="border p-2" value={empresarioId} onChange={e => setEmpresarioId(Number(e.target.value))}>
            <option value={0}>Seleccione empresario</option>
            {empresariosList.map(e => <option key={e.id} value={e.id}>{e.nombre}</option>)}
          </select>
          <input type="date" value={desde} onChange={e => setDesde(e.target.value)} className="border rounded px-2 py-1" />
          <input type="date" value={hasta} onChange={e => setHasta(e.target.value)} className="border rounded px-2 py-1" />
          <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={handleBuscarEmpresario} disabled={loadingEmp}>
            {loadingEmp ? 'Cargando...' : 'Buscar'}
          </button>
          {errorEmp && <span className="text-red-600 text-sm ml-4">{errorEmp}</span>}
        </div>
        {dataEmp && !dataEmp.error && (
          <div>
            <div className="mb-2 font-semibold">Empresario: <span className="text-blue-700">{dataEmp.empresario}</span></div>
            <div className="flex flex-wrap gap-8 mb-4">
              <div>Recorridos: <b>{dataEmp.totalRecorridos}</b></div>
              <div>Ingresos: <b>${dataEmp.totalIngresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</b></div>
              <div>Zonas: <b>{(dataEmp.zonas || []).join(', ')}</b></div>
            </div>
            <div>
              <h3 className="font-bold mb-1">EvoluciÃ³n por decena</h3>
              <table className="min-w-[300px] bg-gray-50 rounded">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="p-2">Decena</th>
                    <th className="p-2">Recorridos</th>
                    <th className="p-2">Ingresos</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(dataEmp.evolucionPorDecena || {}).map(([decena, evo]: any) => (
                    <tr key={decena}>
                      <td className="p-2">{decena}</td>
                      <td className="p-2">{evo.recorridos}</td>
                      <td className="p-2">${evo.ingresos?.toLocaleString(undefined, { maximumFractionDigits: 0 })}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
        {dataEmp && dataEmp.error && (
          <div className="text-red-600 text-sm mt-2">{dataEmp.error}</div>
        )}
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white rounded shadow">
          <thead>
            <tr className="bg-blue-100">
              <th className="p-2">Decena</th>
              <th className="p-2">Zona</th>
              <th className="p-2">Empresario</th>
              <th className="p-2">Recorridos</th>
              <th className="p-2">Monto</th>
            </tr>
          </thead>
          <tbody>
            {filtrados.map((d, i) => (
              <tr key={i} className="border-t">
                <td className="p-2">{d.decena}</td>
                <td className="p-2">{d.zona}</td>
                <td className="p-2">{d.empresario}</td>
                <td className="p-2">{d.recorridos}</td>
                <td className="p-2">${d.monto.toLocaleString()}</td>
              </tr>
            ))}
            {filtrados.length === 0 && (
              <tr><td colSpan={5} className="text-center text-gray-400 p-4">No hay datos para mostrar.</td></tr>
            )}
          </tbody>
        </table>
      </div>
      <div className="mt-8">
        <h2 className="text-lg font-bold mb-2">Ranking de Empresarios</h2>
        <ol className="list-decimal ml-6">
          {empresarios.map(e => (
            <li key={e} className="mb-1">{e} - Total: ${datos.filter(d => d.empresario === e).reduce((acc, d) => acc + d.monto, 0).toLocaleString()}</li>
          ))}
        </ol>
      </div>
    </div>
  );
}
