import React, { useState } from 'react';
import { tripsApi } from '../services/api';

interface CsvImportProps {
  onImportSuccess?: (data: any[]) => void;
  onImportError?: (error: string) => void;
}

const CsvImport: React.FC<CsvImportProps> = ({ onImportSuccess, onImportError }) => {
  const [file, setFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile && selectedFile.type === 'text/csv') {
      setFile(selectedFile);
    } else {
      alert('Por favor selecciona un archivo CSV válido.');
    }
  };

  const handleUpload = async () => {
    if (!file) {
      alert('Por favor selecciona un archivo primero.');
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    try {
      const importedData = await tripsApi.importCsv(file);
      setUploadProgress(100);
      alert(`Importación exitosa: ${importedData.length} viajes importados.`);
      onImportSuccess?.(importedData);
      setFile(null);
      // Limpiar el input de archivo
      const fileInput = document.getElementById('csv-file') as HTMLInputElement;
      if (fileInput) fileInput.value = '';
    } catch (error) {
      console.error('Error uploading file:', error);
      onImportError?.(error instanceof Error ? error.message : 'Error desconocido');
      alert(`Error al importar el archivo: ${error instanceof Error ? error.message : 'Error desconocido'}`);
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Importar Datos CSV</h2>
      
      <div className="mb-4">
        <label htmlFor="csv-file" className="block text-sm font-medium text-gray-700 mb-2">
          Seleccionar archivo CSV
        </label>
        <input
          id="csv-file"
          type="file"
          accept=".csv"
          onChange={handleFileChange}
          className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
          disabled={isUploading}
        />
      </div>

      {file && (
        <div className="mb-4 p-3 bg-green-50 border border-green-200 rounded-md">
          <p className="text-sm text-green-700">
            Archivo seleccionado: <strong>{file.name}</strong>
          </p>
          <p className="text-xs text-green-600">
            Tamaño: {(file.size / 1024).toFixed(2)} KB
          </p>
        </div>
      )}

      {isUploading && (
        <div className="mb-4">
          <div className="w-full bg-gray-200 rounded-full h-2.5">
            <div 
              className="bg-blue-600 h-2.5 rounded-full transition-all duration-300"
              style={{ width: `${uploadProgress}%` }}
            ></div>
          </div>
          <p className="text-sm text-gray-600 mt-1">
            Procesando... {uploadProgress}%
          </p>
        </div>
      )}

      <button
        onClick={handleUpload}
        disabled={!file || isUploading}
        className={`w-full py-2 px-4 rounded-md font-medium transition-colors ${
          !file || isUploading
            ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
            : 'bg-blue-600 text-white hover:bg-blue-700'
        }`}
      >
        {isUploading ? 'Importando...' : 'Importar CSV'}
      </button>

      <div className="mt-4 text-xs text-gray-500">
        <p><strong>Formato esperado:</strong></p>
        <p>Fecha de Viaje, Hora de salida, Origen, Destino, Ruta, Servicio, Tipo de Servicio, Estado, Bus, Nº Patente, AÑO, es-Total Seats, Puntaje Iniciales, Puntaje adicionales, Puntaje Totales, Compensacion, Total Compensado, RUT, Razon Social, Conductor1, Asientos Sucursal, Recaudacion Sucursal, Asientos Camino, Recaudacion Camino, Ingresos manuales</p>
      </div>
    </div>
  );
};

export default CsvImport;
